//模块化的作用是可以让代码更易于管理。下面开发2个模块，然后用主程序加载这2个模块。
exports.division=function(a,b){
    return "------";
}

module.exports={
    division:function(a,b){
        return a/b;
    },
    multipli:function(a,b){
        return a*b;
    }
};

exports.division=function(a,b){
    return "------";
}